import numpy as npimport numpy.matlib as nmimport numpy.linalg as nldef concurrence(rho):    sigmay=nm.asmatrix([[0,-1j],                        [1j,0]]);    tensorsigmay=nm.asmatrix(np.kron(sigmay,sigmay))    conjugaterho=nm.asmatrix(np.conj(rho))    rhotilde=nm.asmatrix(np.dot(tensorsigmay,np.dot(conjugaterho,tensorsigmay)))    rhorhotilde=nm.asmatrix(np.dot(rho,rhotilde))    eiglambdas=np.array(nl.eigvals(rhorhotilde))    lambdas=np.sqrt(eiglambdas)    lambdamax=max(lambdas)    indexlambdamax=nm.asmatrix(np.where(lambdas==lambdamax))    restlambdas=np.delete(lambdas,indexlambdamax[0,0])    conc=max(0,lambdamax-sum(restlambdas))    return(abs(conc))