class EdgeParameters:    def __init__(self, parameter_distributions):        self.parameter_distributions = parameter_distributions        def sample_parameters(self):        sampled = {}        for name, dist in self.parameter_distributions.items():            sampled[name] = dist()        return sampled        def assign(self, graph):        for u, v in graph.edges():            sampled_params = self.sample_parameters()            for name, value in sampled_params.items():                graph.edges[u, v][name] = value        return graph    ### How to use it?"""edge_params = EdgeParameters({    "concurrence": lambda: np.random.uniform(0.6, 1.0),    "probability": lambda: np.random.uniform(0.2, 1.0)})G = edge_params.assign(G)"""