import networkx as nximport mathclass RegularTopology:    def __init__(self, topology_type: str, length: int):        self.topology_type = topology_type.lower()        self.num_rows = length    def create(self):        if self.topology_type == "square":            return self._square_lattice()        elif self.topology_type == "triangular":            return self._triangular_lattice()        elif self.topology_type == "hexagonal":            return self._hexagonal_lattice()        else:            raise ValueError("Unknown topology type")    def _square_lattice(self):        G = nx.grid_2d_graph(self.num_rows, self.num_rows)        G = nx.convert_node_labels_to_integers(G, first_label=0, ordering='sorted')        return G    def _triangular_lattice(self):        G = nx.Graph()        for i in range(self.num_rows):            for j in range(self.num_rows):                node_label = i * self.num_rows + j                x = j * 1.5                y = i * math.sqrt(3)                G.add_node(node_label, pos=(x, -y))                if j > 0:                    G.add_edge(node_label, node_label - 1)                if i > 0:                    G.add_edge(node_label, node_label - self.num_rows)                if i > 0 and j < self.num_rows - 1:                    G.add_edge(node_label, node_label - self.num_rows + 1)        return G    def _hexagonal_lattice(self):        G = nx.grid_2d_graph(self.num_rows, self.num_rows)        G = nx.convert_node_labels_to_integers(G, first_label=0, ordering='sorted')        return Gclass RandomTopology:    def __init__(self, num_nodes, num_edges):        self.num_nodes = num_nodes        self.num_edges = num_edges    def create(self):        return nx.gnm_random_graph(self.num_nodes, self.num_edges)class BarabasiAlbertTopology:    def __init__(self, num_nodes, attached_edges):        self.num_nodes = num_nodes        self.attached_edges = attached_edges    def create(self):        return nx.barabasi_albert_graph(self.num_nodes, self.attached_edges)### How to use it?'''topo = RegularTopology("square", row_size=10)G = topo.create()'''