import networkx as nxdef edge_disjoint_paths(graph, source, target, num_paths):    G = graph.copy()    paths = []    if num_paths == "all":        while True:            try:                path = nx.shortest_path(G, source=source, target=target)                paths.append(path)                edges_to_remove = [                    (path[i], path[i + 1]) for i in range(len(path) - 1)                ]                G.remove_edges_from(edges_to_remove)            except nx.NetworkXNoPath:                break    elif isinstance(num_paths, int) and num_paths > 0:        for _ in range(num_paths):            try:                path = nx.shortest_path(G, source=source, target=target)                paths.append(path)                edges_to_remove = [                    (path[i], path[i + 1]) for i in range(len(path) - 1)                ]                G.remove_edges_from(edges_to_remove)            except nx.NetworkXNoPath:                break    else:        raise ValueError("num_paths must be a positive integer or 'all'")    return paths### How to use it?'''paths = edge_disjoint_paths(G, s, d, num_paths=5)'''